import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

let curDate = new Date(2021 , 5, 13, 3);
curDate = curDate.getHours();
let greeting = '';
const cstyle = {};


if(curDate >= 1 && curDate<12) {
    greeting = 'Good Morning';
    cstyle.color = 'blue';
} else if(curDate>=12 && curDate < 19){
    greeting = 'Good Afternoon';
    cstyle.color = 'yellow';
} else {
    greeting = 'Good Night';
    cstyle.color = 'black';
}


ReactDOM.render(
    <>
    <div>

    <h1> Hello, <span style={cstyle}> {greeting} </span> </h1>,
    </div>
     </>,


    document.getElementById("root")
);
